// Temperature chart for the day
const temperatureChart = function(hours) {
	var xValues = [
		'','01:00','02:00','03:00','04:00','05:00','06:00','07:00','08:00',
		'09:00','10:00','11:00','12:00','13:00','14:00','15:00','16:00','17:00',
		'18:00','19:00','20:00','21:00','22:00','23:00'];
	var yValues = 
	[	
		`${hours[0].feelslike_c}`,
		`${hours[1].feelslike_c}`,
		`${hours[2].feelslike_c}`,
		`${hours[3].feelslike_c}`,
		`${hours[4].feelslike_c}`,
		`${hours[5].feelslike_c}`,
		`${hours[6].feelslike_c}`,
		`${hours[7].feelslike_c}`,
		`${hours[8].feelslike_c}`,
		`${hours[9].feelslike_c}`,
		`${hours[10].feelslike_c}`,
		`${hours[11].feelslike_c}`,
		`${hours[12].feelslike_c}`,
		`${hours[13].feelslike_c}`,
		`${hours[14].feelslike_c}`,
		`${hours[15].feelslike_c}`,
		`${hours[15].feelslike_c}`,
		`${hours[16].feelslike_c}`,
		`${hours[17].feelslike_c}`,
		`${hours[18].feelslike_c}`,
		`${hours[19].feelslike_c}`,
		`${hours[20].feelslike_c}`,
		`${hours[21].feelslike_c}`,
		`${hours[22].feelslike_c}`,
		`${hours[23].feelslike_c}`,
	];
	
	new Chart("myChart", {
		type: "line",
	  	data: {
			labels: xValues,
			datasets: [{
				label: 'Temperature',
				backgroundColor: "rgba(225, 225, 18, 0.67)",
		  		borderColor: "rgba(0,0,0,0.2)",
		  		data: yValues
			}]
	  	},
		options: {
			scales: {
				xAxes: [{
					gridLines: {
						color: "rgba(0, 0, 0, 0)",
					},
					ticks: {
						fontColor: "#747b80",
					}
				}],
				yAxes: [{
					suggestedMax: 40,
					suggestedMin: 0,
					gridLines: {
						color: "rgba(0, 0, 0, 0)",
					},
					ticks: {
						fontColor: "#747b80",
					}   
				}],
			},
			legend: {
				display: false
			},
			tooltips: {
				enabled: false
			},
			maintainAspectRatio: false
		}
	});
}

const showResult = (queryResponse) => {
	//console.log({queryResponse});
	//console.log(queryResponse.forecast.forecastday[0].hour);

	const location = document.getElementById('js-location');
	const date = document.getElementById('js-date');
	const temperature = document.getElementById('js-temperature');
	const feelslike = document.getElementById('js-feelslike');
	const humidity = document.getElementById('js-humidity');
	const wind = document.getElementById('js-wind');
	const toggleBackground = document.getElementById('toggle');

	location.innerHTML = `<div"> 
								<svg class="c-weather--location__icon" xmlns="http://www.w3.org/2000/svg" height="24px" viewBox="0 0 24 24" width="24px" fill="#FAFAFA"><path d="M0 0h24v24H0z" fill="none"/><path d="M12 12c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2zm6-1.8C18 6.57 15.35 4 12 4s-6 2.57-6 6.2c0 2.34 1.95 5.44 6 9.14 4.05-3.7 6-6.8 6-9.14zM12 2c4.2 0 8 3.22 8 8.2 0 3.32-2.67 7.25-8 11.8-5.33-4.55-8-8.48-8-11.8C4 5.22 7.8 2 12 2z"/></svg>
							</div>${queryResponse.location.name}`;
	date.innerHTML = `${queryResponse.location.localtime}`;
	humidity.innerHTML = `${queryResponse.current.humidity}%`;
	wind.innerHTML = `${queryResponse.current.wind_kph} kph`;

	var d = new Date;
	//console.log(queryResponse.forecast.forecastday[0].hour[d.getHours()].is_day);

	// Send current condition and current hour of the day
	weatherconditionIcon(queryResponse.current.condition.text, queryResponse.forecast.forecastday[0].hour[d.getHours()].is_day);
	
	// Default Using Celsius
	temperature.innerHTML = `${queryResponse.current.temp_c}°`;
	feelslike.innerHTML =`Feels like ${queryResponse.current.feelslike_c}°`;

	// Change Temperatures to Celsius
	document.getElementById('toggle-degrees').addEventListener("click", function(){
		temperature.innerHTML = `${queryResponse.current.temp_c}°`;
		feelslike.innerHTML =`Feels like ${queryResponse.current.feelslike_c}°`;
	});

	// Change Temperatures to Farenheit
	document.getElementById('toggle-degrees2').addEventListener("click", function(){
		temperature.innerHTML = `${queryResponse.current.temp_f}°`;
		feelslike.innerHTML =`Feels like ${queryResponse.current.feelslike_f}°`;
	});

	// Show temperature chart for today
	temperatureChart(queryResponse.forecast.forecastday[0].hour);

	// Darkmode Toggle
	toggleBackground.addEventListener('click', function(){
		c = counter();

		if (c == 0){
			document.body.style.background = 'white';
			document.getElementById("js-card").style.background = 'white';
			document.getElementById("js-card").style.color = '#222526';
		}
		
		if (c == 1){
			document.body.style.background = '#16181a';
			document.getElementById("js-card").style.background = '#16181a';
			document.getElementById("js-card").style.color = '#FFFF';
		}
	}); 
};

const get = (url) => fetch(url).then((r) => r.json());

const getAPI= async (lat, lon) => {
	const endPoint = `http://api.weatherapi.com/v1/forecast.json?key=${apiKey}&q=${city}&days=1&aqi=no&alerts=no`;
	const response = await get(endPoint);

	showResult(response);
};

